import {Link} from 'react-router-dom';
import React from 'react'

let getTitle = (note) => {
  const title = note.body.split('\n')[0]
  return title
}

function ListItem({note}) {

  return (
    <Link to={`/note/${note.id}`}>
      <div className='notes-list-item'>
          <h3>{getTitle}</h3>
      </div>
    </Link>
  )
}

export default ListItem
